import React, { useEffect } from 'react';
import './sidenav.css';
import { withRouter } from 'react-router-dom';

function SideNav(props) {
    const { inbox, sent, history, user } = props;

    useEffect({

    }, []);

    const handleMailSelect = (event, call) => {
        let sideNavListElem = document.getElementsByClassName('side-nav-list');
        for (let index = 0; index < sideNavListElem.length; index++) {
            sideNavListElem[index].classList.remove('active');
        }
        event.target.classList.add('active');
        history.push(`/mailApp/${call}/${user.userId}`);
    }

    return (
        <div className='side-nav-bar'>
            <button id='composeMailBtn' onClick={() => props.composeMail()}>Compose</button>
            <ul>
                <li id="inboxList" className="side-nav-list" onClick={(event) => handleMailSelect(event, 'inbox')}>
                    Inbox
                    <span className='unread-mail' id='unreadMailInbox'>{inbox}</span>
                </li>
                <li id="sentList" className="side-nav-list" onClick={(event) => handleMailSelect(event, 'sent')}>
                    Sent
                    <span className='unread-mail' id='unreadMailSent'>{sent}</span>
                </li>
            </ul>
        </div>
    );
}

export default withRouter(SideNav);
