import React, { useEffect } from 'react';
import './sidenav.css';
import { withRouter } from 'react-router-dom';

function SideNav(props) {
    const { inbox, sent, history, user, location } = props;
    if (location.pathname.indexOf('/inbox/') > 0) {
        document.querySelector('#inboxList').classList.add('active');
    } else if (location.pathname.indexOf('/sent/') > 0) {
        document.querySelector('#sentList').classList.add('active');
    }

    return (
        <div className='side-nav-bar'>
            <button id='composeMailBtn' onClick={() => props.composeMail()}>Compose</button>
            <ul>
                <li id="inboxList" className="side-nav-list" onClick={() => history.push(`/mailApp/inbox/${user.userId}`)}>
                    Inbox
                    <span className='unread-mail' id='unreadMailInbox'>{inbox}</span>
                </li>
                <li id="sentList" className="side-nav-list" onClick={() => history.push(`/mailApp/sent/${user.userId}`)}>
                    Sent
                    <span className='unread-mail' id='unreadMailSent'>{sent}</span>
                </li>
            </ul>
        </div>
    );
}

export default withRouter(SideNav);
