import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { sendMail } from './../../actions';
import './composemail.css';

export default function ComposeMail(props) {
    const { userId } = props.user;
    const userList = useSelector(state => state.accReducer);
    const dispatch = useDispatch();
    let userMailIdList = [];
    let selectUserIdList = [];

    const handleRecipientChange = (event) => {
        userMailIdList = [];
        let tempVal = event.target.value;
        if (tempVal.length >= 3) {
            userMailIdList = Object.keys(userList).filter(userId => {
                return userList[userId].firstName.toLowerCase().startsWith(tempVal.toLowerCase());
            });
        }
    }

    const handleUserSelect = (event) => {
        let selectUserId = event.target.getAttribute('data-key');
        selectUserIdList.push(selectUserId);
    }

    const handleSendMail = () => {
        let currDate = new Date()
        let recipientsId = [];
        let recipientList = document.querySelector('#recipientList').children
        for (let i = 0; i < recipientList.length; i++) {
            recipientsId(recipientList[0].getAttribute('data-key'));
        }
        let mailObj = {
            senderId: userId,
            recipientsId: recipientsId,
            mailSubject: document.getElementById('subjectInp').value,
            mailBody: document.getElementById('messageInp').value,
            mailTime: `${currDate.getMonth() + 1}/${currDate.getcurrDate()}/${currDate.getFullYear()} ${currDate.getHours()}:${currDate.getMinutes()}`
        }
        dispatch(sendMail(mailObj))
        props.composeMail();
    }

    return (
        <div className='compose-mail'>
            <div className='header'>
                New Message
                <button id="closeComposeMail" onClick={() => props.composeMail()}>X</button>
            </div>
            <div className='container recipient-container'>
                <div className='recipientList'>
                    {
                        selectUserIdList.map(el => {
                            return (
                                <span className='recipient' data-key={el} >
                                    {userList[el].firstName}
                                    <span className='recipient-del'>X</span>
                                </span>
                            )
                        })
                    }
                </div>
                <textarea id="recipientInp" name='recipients' placeholder='Recipients' onChange={handleRecipientChange} />
            </div>
            <div className='container subject-container'>
                <input id="subjectInp" type='text' name='subject' placeholder='Subject' />
            </div>
            <div className='container message-container'>
                <textarea id="messageInp" name='message' placeholder='Type Something here..' />
            </div>
            <div className='container footer'>
                <button id="sendMailBtn" onClick={handleSendMail}>Send</button>
            </div>
            <div className='user-mail-list'>
                {
                    userMailIdList.map(el => {
                        return (
                            <div className='user-mail-details' data-key={el} onClick={handleUserSelect}>
                                <div className='user-mail-name'>{userList[el].firstName}</div>
                                <div className='user-mail-id'>{userList[el].emailId}</div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    );
}
