import React, { useEffect, useState } from 'react';
import MailList from './mail-list/MailList';
import SideNav from './side-nav/SideNav';
import Header from './header/Header';
import { useStore } from 'react-redux';

export default function MailDetails({ match }) {
    const styles = {
        width: '100%',
        height: '90vh',
        overflow: 'hidden',
        display: 'inline-flex',
    }
    const store = useStore();
    const [userId, setUserId] = userState('');
    useEffect(() => {
        setUserId(match.params.userId);
        console.log(userId);
    }, []);

    return (
        <div className='mail-dets-content'>
            <Header />
            <div className='mail-details' style={styles}>
                <SideNav />
                <MailList />
            </div>
        </div>
    );
}
