import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import './maillist.css';

export default function MailList(props) {
    const { user, type } = props;
    const [mailList, setMailList] = useState([]);

    let mails = [];
    if (type == 'inbox') {
        mails = useSelector(state => state.mailReducer[user.userId].inboxmail);
    } else if (type == 'sent') {
        mails = useSelector(state => state.mailReducer[user.userId].inboxmail);
    }
    setMailList(mails);

    return (
        <div className='mail-dets-list-content'>
            <div className='mail-dets-list'>
                <div className='mail-subject'>doodblue innovations</div>
                <div className='mail-body'>Interview Task | Front-End Developer(React.js)</div>
                <div className='mail-time'>11:53 PM</div>
            </div>
            <div className='mail-dets-list unread-mail-body'>
                <div className='mail-subject'>doodblue innovations</div>
                <div className='mail-body'>Interview Task | Front-End Developer(React.js)</div>
                <div className='mail-time'>11:53 PM</div>
            </div>
        </div>
    );
}
